package com.example.myapplication

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import kotlinx.android.synthetic.main.activity_libaray.*

class LibarayActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_libaray)


        Glide.with(this@LibarayActivity)
            .load(
                "data:image/jpeg;base64,"
            )
            //앱을 만들 때 기본적으로 앱이 인터넷을 사용하는 것을 허락하지 않는다. 권한을 부여해야 한다 >> Manifest에 들어간다.
            //android permission internet이라고 구글에 치면 하는 코드가 나오니 복붙해서 추가하자
            .fitCenter() // 자동완성으로 한번 해보는 것도 좋다!!!!!!!! 여러가지 것들이 많음!
            .into(glide)

    }
}